    private void jl_cancionesMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jl_cancionesMouseClicked
        if (jl_canciones.getSelectedIndex() >= 0) {
            if (evt.isMetaDown()) {
                popup_cancion.show(evt.getComponent(), evt.getX(), evt.getY());
            }
        }
    }//GEN-LAST:event_jl_cancionesMouseClicked

    private void jb_addPlaylistMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jb_addPlaylistMouseClicked
        DefaultListModel modelo = (DefaultListModel) jl_playlists.getModel();
        String n = JOptionPane.showInputDialog(jd_usuario, "Nombre de la playlist");
        String g = JOptionPane.showInputDialog(jd_usuario, "Genero Musical");
        playlist p = new playlist(n, g, "2019");
        modelo.addElement(p);
        
        usuarios.get(pos).getPlaylists().add(p);
        jl_playlists.setModel(modelo);
        jList1.setModel(modelo);

    }//GEN-LAST:event_jb_addPlaylistMouseClicked

    private void jButton3MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton3MouseClicked
        if (rb_a.isSelected()) {
            artistas.add(new artista(tf_NAME.getText(), tf_APELLIDO.getText(), tf_USER.getText(), tf_PASS.getText(), DATECHOOSER.getSelectedDate().getTime()));
            JOptionPane.showMessageDialog(jd_registro, "Registrado con exito");
            tf_NAME.setText("");
            tf_APELLIDO.setText("");
            tf_PASS.setText("");
            tf_USER.setText("");
            
        } else {
            usuarios.add(new usuario(tf_NAME.getText(), tf_APELLIDO.getText(), tf_USER.getText(), tf_PASS.getText(), DATECHOOSER.getSelectedDate().getTime()));
            JOptionPane.showMessageDialog(jd_registro, "Registrado con exito");
            tf_NAME.setText("");
            tf_APELLIDO.setText("");
            tf_PASS.setText("");
            tf_USER.setText("");
            
        }
        
    }//GEN-LAST:event_jButton3MouseClicked

